import React, { useContext, useEffect, useState } from 'react';
import SideBar from "../components/CategoryPage/SideBar";
import MyBreadcrumbs from "../components/CategoryPage/MyBreadcrumbs";
import { fetchCategories, fetchGoods,fetchBrands, fetchOneGood } from "../http/shopAPI";
import { Context } from "../index";
import { useParams } from 'react-router-dom';

const GoodInfo = () => {

    const params = useParams()
    const {good} = useContext(Context)

    useEffect(()=>{
        fetchCategories().then((data) => good.setCategories(data))
        fetchBrands().then(data=>good.setBrands(data))

        fetchOneGood(params.id).then((data)=>{
            good.setGood(data)
                const caregoryName = good.getCategoryNameById(data.id)
                good.setBreadcrumbsLinks([
                    { title: 'Главная', link: '/', active: false },
                    { title: 'Каталог', link: '/catalog', active: false },
                    { title: caregoryName, link: '/catalog/category/' + params.id, active:false },
                    {title: data.model,link:'/',active:true}
                ])
            })

    },[])

    return (
        <main className="catalog-main">
            <SideBar />
            <div className="main">
                <div className="update-main">
                    <MyBreadcrumbs links={breadcrumbsLinks} />
                    
                </div>
            </div>
        </main>
    )
};

export default GoodInfo;
