import { useContext, useEffect, useState } from "react"
import GoodsList from "../components/BasketPage/GoodsList"
import { Context } from "../index"

const Catalog = () => {
    const { basket } = useContext(Context)

    const [myBasket,setMyBasket] = useState(basket);

    const updateBasket = ()=>{
        console.log(1)
        setMyBasket(basket);
    }

    return (
        <div id="basket-root">
            <div className="basket-title-block" data-entity="basket-total-block">
                {myBasket.isEmpty ?
                    <div className="basket-title">
                        <div style={{color:'#ccc'}}>Ваша корзина пуста</div>
                    </div> :
                    <div className="basket-title">
                        <h1>Корзина:&nbsp;</h1>

                        <div>количество {myBasket.totalCount} на сумму {myBasket.totalSum}</div>
                    </div>

                }
            </div>
            <GoodsList onChange={updateBasket}/>
        </div>
    )
}

export default Catalog
