import React, { useContext, useEffect, useState } from 'react';
import SideBar from "../components/CategoryPage/SideBar";
import MyBreadcrumbs from "../components/CategoryPage/MyBreadcrumbs";
import { fetchCategories, fetchGoods, fetchBrands, fetchOneGood } from "../http/shopAPI";
import { Context } from "../index";
import { useParams } from 'react-router-dom';

const GoodInfo = () => {

    const params = useParams()
    const { good } = useContext(Context)

    useEffect(() => {
        fetchCategories().then((c) => {
            good.setCategories(c)
            fetchOneGood(params.id).then((g) => {
                good.setGood(g)

                good.setBreadcrumbsLinks([
                    { title: 'Главная', link: '/', active: false },
                    { title: 'Каталог', link: '/catalog', active: false },
                    { title: g.category.name, link: '/catalog/category/' + params.id, active: false },
                    { title: g.model, link: '/', active: true }
                ])
            })
        })
        fetchBrands().then(data => good.setBrands(data))



    }, [])

    return (
        <main className="catalog-main">
            <SideBar />
            <div className="main">
                <div className="update-main">
                    <MyBreadcrumbs links={good.breadcrumbsLinks} />
                    <div className='product-page'>
                        <div className='product-item-left'>

                        </div>
                        <div className='product-item-right'>
                            <div className='product-item-right-top'>
                                <h1 dangerouslySetInnerHTML={{ __html: good.good.model }}></h1>
                                <div class="specifications">
                                    <div class="specifications-item" itemprop="description">
                                        <div class="specifications-item-text">Производитель:</div>
                                        <div class="specifications-item-name">{good.good.brand.name}</div>
                                    </div>
                                    <div class="specifications-item">
                                        <div class="specifications-item-text">Артикул:</div>
                                        <div class="specifications-item-name">{good.good.article}</div>
                                    </div>
                                </div>
                                <div className='prices'>
                                    <div className='prices-item'>
                                        <div className='prices-item-text'>Цена</div>
                                        <div className='prices-item-name price-value'>{good.good.price} Р</div>
                                    </div>
                                    <div className='prices-item'></div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </main>
    )
};

export default GoodInfo;
